INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(1 , 4,  'es', 'La estructura de gobierno es plenamente funcional, está dirigida por el gobierno, consulta con otros ministerios y supervisa la implementación de la salud digital sobre la base de un plan de trabajo.');
