INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 ,  4, 'es', 'La salud digital se está aplicando como parte de las estrategias y/o planes nacionales de salud u otras estrategias y/o planes nacionales pertinentes.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 ,  5, 'es', 'La salud digital se implementa y se evalúa y optimiza periódicamente en las estrategias y/o planes nacionales de salud u otras estrategias y/o planes nacionales relevantes.');
