INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(17, -1, 'fr', 'Non disponible');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(17,  1, 'fr', 'Il n''y a pas de formation disponible dans le pays pour le personnel de santé numérique.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(17,  2, 'fr', 'Évaluation des besoins en personnel de santé numérique, identification des lacunes et options de formation en cours d''élaboration.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(17,  3, 'fr', 'Une formation professionnelle est disponible, mais les cursus ne sont pas encore déployés.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(17,  4, 'fr', 'Des professionnels de la santé formés à l''informatique sont disponibles et déployés, mais des lacunes essentielles subsistent en matière de personnel.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(17,  5, 'fr', 'Un nombre suffisant de professionnels de la santé numériques formés et disponibles pour répondre aux besoins nationaux en matière de santé numérique.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18, -1, 'fr', 'Non disponible');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18,  1, 'fr', 'Il n''existe pas de formation en informatique ou en systèmes d''information sanitaire dans le pays.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18,  2, 'fr', 'Évaluation des besoins de la main-d''œuvre en informatique de la santé, détermination des lacunes et des options de formation en cours d''élaboration.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18,  3, 'fr', 'Une formation professionnelle en informatique de la santé est disponible, mais les cursus ne sont pas encore déployés.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18,  4, 'fr', 'Des professionnels de l''informatique formés sont disponibles et déployés, mais il subsiste des lacunes essentielles en matière de personnel.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18,  5, 'fr', 'Un nombre suffisant de professionnels de l''informatique de la santé formés et disponibles pour répondre aux besoins du système national d''information sur la santé');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(19, -1, 'fr', 'Non disponible');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(19,  1, 'fr', 'Il n''existe pas de stratégie, de politique ou de guide sur la main-d''œuvre qui reconnaît la santé numérique. La répartition du personnel de santé numérique est ponctuelle.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(19,  2, 'fr', 'Une évaluation nationale des besoins montre le nombre et les types de compétences nécessaires pour soutenir la santé numérique, en mettant explicitement l''accent sur la formation des cadres des agents de santé féminins.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(19,  3, 'fr', 'Les rôles et les responsabilités du personnel de santé numérique sont mis en correspondance avec les effectifs et les plans de carrière du gouvernement et 25 à 50 % des effectifs de santé numérique nécessaires du secteur public sont en place.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(19,  4, 'fr', 'Il existe une politique et un plan stratégique en matière de ressources humaines qui définissent les compétences et les fonctions nécessaires pour appuyer la santé numérique, en mettant explicitement l''accent sur la formation de cadres d''agents de santé féminins et d''environ 50 à 75 % des effectifs en santé numérique du secteur public en place.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(19,  5, 'fr', 'Un plan à long terme est en place pour former et maintenir en poste le personnel possédant les compétences nécessaires pour maintenir la santé numérique aux niveaux national et infranational, en mettant explicitement l''accent sur la formation de cadres d''agents de santé féminins dont on estime que plus de 75 % des postes sont pourvus. Des systèmes de gestion du rendement sont en place pour assurer la croissance et la viabilité de la main-d''œuvre en santé numérique, avec un approvisionnement suffisant pour répondre aux besoins en santé numérique et un faible roulement du personnel.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(20, -1, 'fr', 'Non disponible');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(20,  1, 'fr', 'Il n''existe pas de cadre d''architecture de santé numérique (cybersanté) ni d''échange d''information sur la santé.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(20,  2, 'fr', 'Un cadre national d''architecture de santé numérique et/ou un échange d''information sur la santé ont été proposés, mais non approuvés, y compris les couches sémantique, syntaxique et organisationnelle.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(20,  3, 'fr', 'L''architecture nationale de la santé numérique et/ou l''échange d''information sur la santé est fonctionnelle et offre des fonctions de base comme l''authentification, la traduction, le stockage et l''entreposage, un guide sur les données disponibles et la façon d''y accéder et l''interprétation des données.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(20,  4, 'fr', 'Le gouvernement dirige, gère et applique la mise en œuvre de l''architecture nationale de la santé numérique et/ou l''échange d''information sur la santé, qui sont entièrement mis en œuvre conformément aux normes de l''industrie.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(20,  5, 'fr', 'L''architecture nationale de la santé numérique et/ou l''échange d''information sur la santé fournit des fonctions d''échange de données de base et est périodiquement révisée et mise à jour pour répondre aux besoins de l''architecture numérique de la santé en évolution. Il y a l''apprentissage continu, l''innovation et le contrôle de la qualité. Les données sont activement utilisées pour la planification stratégique et la budgétisation de la santé nationale.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(21, -1, 'fr', 'Non disponible');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(21,  1, 'fr', 'Il n''existe pas de normes de santé numérique et d''information sur la santé pour l''échange de données, la transmission, la messagerie, la sécurité, la confidentialité et le matériel.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(21,  2, 'fr', 'Certaines normes de santé numérique et d''information sur la santé pour l''échange de données, la transmission, la messagerie, la sécurité, la confidentialité et le matériel informatique ont été adoptées ou sont utilisées.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(21,  3, 'fr', 'Des normes relatives à l''échange, à la transmission, à la messagerie, à la sécurité, à la protection des renseignements personnels et au matériel informatique ont été publiées et diffusées dans le pays sous la direction du gouvernement.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(21,  4, 'fr', 'Des normes relatives à l''échange, à la transmission, à la messagerie, à la sécurité, à la protection des renseignements personnels et au matériel informatique ont été publiées et diffusées dans le pays sous la direction du gouvernement.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(21,  5, 'fr', 'Les normes de données sont régulièrement mises à jour et les données sont activement utilisées pour le suivi et l''évaluation du système de santé ainsi que pour la planification stratégique et la budgétisation de la santé nationale.');
