INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23, -1, 'fr', 'Non disponible');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  1, 'fr', 'Il n''y a pas de plan articulé pour soutenir la fourniture et l''entretien de l''infrastructure de santé numérique (y compris l''équipement - ordinateurs, tablettes, téléphones, fournitures, logiciels, appareils, etc.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  2, 'fr', 'Un plan de soutien à la fourniture et à l''entretien de l''infrastructure pour la santé numérique (y compris l''équipement (ordinateurs, tablettes, téléphones, fournitures, logiciels, appareils, etc.) a été élaboré, mais n''a pas été mis en œuvre.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  3, 'fr', 'Un plan de soutien à la fourniture et à l''entretien de l''infrastructure pour la santé numérique (y compris l''équipement - ordinateurs, tablettes, téléphones, fournitures, logiciels, appareils, etc.) a été mis en œuvre en partie, mais pas toujours avec une estimation de 0 à 25 % de l''infrastructure nécessaire, disponible et en service dans le secteur des services de santé publique.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  4, 'fr', 'Un plan d''appui à la fourniture et à l''entretien de l''infrastructure pour la santé numérique (y compris l''équipement - ordinateurs, tablettes, téléphones, fournitures, logiciels, appareils, etc.) a été mis en œuvre partiellement et conformément à l''estimation de 25 à 50 % de l''infrastructure nécessaire, disponible et en service dans le secteur des services de santé publique..');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  5, 'fr', 'l''infrastructure de santé numérique (y compris l''équipement - ordinateurs, tablettes, téléphones, fournitures, logiciels, appareils, etc.) est disponible, utilisée, régulièrement entretenue et mise à niveau dans plus de 75 % du secteur des services de santé publique.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24, -1, 'fr', 'Non disponible');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  1, 'fr', 'Les domaines prioritaires nationaux ne sont soutenus par la santé numérique à aucune échelle.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  2, 'fr', 'Peu de domaines prioritaires nationaux sont appuyés par la santé numérique et la mise en œuvre a commencé (< 25 % des domaines prioritaires).');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  3, 'fr', 'Certains domaines prioritaires nationaux appuyés par des systèmes de santé numériques à l''échelle (25 à 50 % des domaines prioritaires).');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  4, 'fr', 'La majorité, mais pas tous les domaines prioritaires nationaux (50 à 75 % des domaines prioritaires) sont soutenus par des systèmes de santé numériques à l''échelle.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  5, 'fr', 'Tous les domaines prioritaires à l''échelle nationale sont soutenus par des systèmes de santé numériques à l''échelle nationale (>75 %) avec des résultats et des systèmes de suivi et d''évaluation.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(25, -1, 'fr', 'Non disponible');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(25,  1, 'fr', 'Les registres du système de santé : des prestataires, administrateurs et établissements publics (et privés s''il y a lieu) identifiables de façon unique ne sont pas disponibles, accessibles et à jour.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(25,  2, 'fr', 'Des registres du système de santé : des prestataires, des administrateurs et des établissements publics (et privés s''il y a lieu) identifiables de façon unique sont en cours d''élaboration, mais ne sont pas disponibles pour utilisation.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(25,  3, 'fr', 'Les registres du système de santé: des prestataires, des administrateurs et des établissements publics (et privés s''il y a lieu) identifiables de façon unique sont disponibles pour utilisation, mais ils sont incomplets, partiellement disponibles, utilisés de façon sporadique et tenus de façon irrégulière.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(25,  4, 'fr', 'Les registres du système de santé: des prestataires, administrateurs et établissements publics (et privés s''il y a lieu) identifiables de façon unique sont disponibles, utilisés, mis à jour et tenus à jour régulièrement. Les données sont géolocalisées pour permettre la cartographie SIG.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(25,  5, 'fr', 'Les registres du système de santé : des prestataires, des administrateurs et des établissements publics (et privés s''il y a lieu) identifiables de façon unique sont disponibles, mis à jour à l''aide de données géolocalisées et utilisés pour la planification stratégique et la budgétisation du système et des services de santé.');
