INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(1 ,-1,  'pt', 'Não disponível');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(1 , 1,  'pt', 'Não existe nenhum órgão de coordenação e/ou a estrutura emergente de governança para a saúde digital é constituída caso a caso.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(1 , 2,  'pt', 'A estrutura de governação é formalmente constituída, embora não totalmente funcional, ou reúne-se regularmente.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(1 , 3,  'pt', 'A estrutura de governança e quaisquer grupos de trabalho relacionados têm um âmbito de trabalho (SOW) e realizam reuniões regulares com a participação e/ou consulta das partes interessadas.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(1 , 4,  'pt', 'A estrutura de governação é totalmente funcional, conduzida pelo governo, consulta outros ministérios e monitoriza a implementação da saúde digital com base num plano de trabalho.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(1 , 5,  'pt', 'A estrutura de governação da saúde digital é institucionalizada, consulta outros ministérios e monitoriza a implementação da saúde digital. Está relativamente protegida de interferências ou mudanças organizacionais. A estrutura de governação e os seus grupos de trabalho técnicos enfatizam o equilíbrio de género nos membros.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 , -1, 'pt', 'Não disponível');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 ,  1, 'pt', 'A saúde digital não está incluída na estratégia nacional de saúde. É implementada de forma ad hoc em programas de saúde.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 ,  2, 'pt', 'Há alguma discussão sobre a inclusão da saúde digital na saúde nacional ou em outras estratégias ou planos nacionais relevantes. A linguagem proposta para a inclusão da saúde digital na saúde nacional ou em estratégias e/ou planos nacionais relevantes já foi elaborada e está sendo revisada.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 ,  3, 'pt', 'A saúde digital está incluída na saúde nacional ou em estratégias e/ou planos nacionais relevantes.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 ,  4, 'pt', 'A saúde digital está a ser implementada como parte integrante das estratégias e/ou planos nacionais de saúde ou de outras estratégias e/ou planos nacionais relevantes.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 ,  5, 'pt', 'A saúde digital é implementada e periodicamente avaliada e otimizada na saúde nacional ou em outras estratégias e/ou planos nacionais relevantes.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(3 , -1, 'pt', 'Não disponível');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(3 ,  1, 'pt', 'Não existe uma estratégia ou quadro de saúde digital. Projeto de estratégia ou estrutura de saúde digital desenvolvido, mas não revisto oficialmente.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(3 ,  2, 'pt', 'Estratégia ou quadro nacional de saúde digital aprovado.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(3 ,  3, 'pt', 'Plano nacional de saúde digital orçamentado desenvolvido e aprovado.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(3 ,  4, 'pt', 'Estratégia nacional de saúde digital e plano orçamentado parcialmente implementado com recursos para assegurar a sua plena implementação.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(3 ,  5, 'pt', 'Estratégia nacional de saúde digital e plano orçamentado totalmente implementado com o planeamento em curso para o próximo ciclo de 3-5 anos.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(4 , -1, 'pt', 'Não disponível');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(4 ,  1, 'pt', 'Nenhuma rubrica orçamental para a saúde digital disponível. Existe uma rubrica orçamental para a saúde digital, mas a proporção não está disponível.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(4 ,  2, 'pt', 'Menos de 1 %');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(4 ,  3, 'pt', '1-3%');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(4 ,  4, 'pt', '3-5%');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(4 ,  5, 'pt', 'Superior a 5%');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(5 , -1, 'pt', 'Não disponível');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(5 ,  1, 'pt', 'Não existe nenhuma lei sobre segurança de dados (armazenamento, transmissão, utilização) que seja relevante para a saúde digital.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(5 ,  2, 'pt', 'Existe uma lei sobre a segurança dos dados (armazenamento, transmissão, utilização) que é relevante para a saúde digital que foi proposta e está a ser revista.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(5 ,  3, 'pt', 'Existe uma lei sobre a segurança dos dados (armazenamento, transmissão, utilização) que é relevante para a saúde digital que foi aprovada, mas que ainda não foi totalmente implementada.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(5 ,  4, 'pt', 'Existe uma lei sobre a segurança dos dados (armazenamento, transmissão, utilização) que é relevante para a saúde digital que foi implementada, mas não aplicada de forma consistente.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(5 ,  5, 'pt', 'Existe uma lei sobre a segurança dos dados (armazenamento, transmissão, utilização) que é relevante para a saúde digital e que foi implementada e aplicada de forma consistente.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(6 , -1, 'pt', 'Não disponível');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(6 ,  1, 'pt', 'Não existe nenhuma lei que proteja a privacidade individual, que regule a propriedade, o acesso e a partilha de dados de saúde digitais identificáveis individualmente.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(6 ,  2, 'pt', 'Existe uma lei para proteger a privacidade individual, que rege a propriedade, o acesso e a partilha de dados de saúde digitais identificáveis individualmente que foi proposta e está a ser revista.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(6 ,  3, 'pt', 'Existe uma lei para proteger a privacidade individual, que rege a propriedade, o acesso e a partilha de dados de saúde digitais individualmente identificáveis que foram aprovados, mas que ainda não foram plenamente implementados.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(6 ,  4, 'pt', 'Existe uma lei para proteger a privacidade individual, que rege a propriedade, o acesso e a partilha de dados de saúde digitais individualmente identificáveis que foi implementada, mas não aplicada de forma coerente.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(6 ,  5, 'pt', 'Existe uma lei para proteger a privacidade individual, que rege a propriedade, o acesso e a partilha de dados de saúde digitais identificáveis individualmente, que foi implementada e é aplicada de forma coerente.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(7 , -1, 'pt', 'Não disponível');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(7 ,  1, 'pt', 'Não existem protocolos, políticas, quadros ou processos aceites que regulem a utilização de dispositivos médicos conectados e de serviços de saúde digitais (por exemplo, telemedicina, aplicações), especialmente em relação à segurança, integridade dos dados e qualidade dos cuidados.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(7 ,  2, 'pt', 'Foram propostos e estão a ser analisados protocolos, políticas, quadros ou processos aceites que regem a utilização de dispositivos médicos conectados e de serviços de saúde digitais (por exemplo, telemedicina, aplicações), especialmente em relação à segurança, integridade dos dados e qualidade dos cuidados de saúde.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(7 ,  3, 'pt', 'Foram aprovados protocolos, políticas, quadros ou processos aceites que regem a utilização de dispositivos médicos conectados e de serviços digitais de saúde (por exemplo, telemedicina, aplicações), especialmente em relação à segurança, integridade dos dados e qualidade dos cuidados, mas que não são plenamente implementados.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(7 ,  4, 'pt', 'Foram implementados protocolos, políticas, quadros ou processos aceites que regem a utilização de dispositivos médicos conectados e de serviços digitais de saúde (por exemplo, telemedicina, aplicações), especialmente em relação à segurança, integridade dos dados e qualidade dos cuidados, mas não foram aplicados de forma coerente.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(7 ,  5, 'pt', 'Os protocolos, políticas, quadros ou processos aceites que regem a utilização de dispositivos médicos conectados e de serviços digitais de saúde (por exemplo, telemedicina, aplicações), especialmente em relação à segurança, integridade dos dados e qualidade dos cuidados, foram implementados e são aplicados de forma coerente.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(8 , -1, 'pt', 'Não disponível');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(8 ,  1, 'pt', 'Não existem protocolos, políticas, quadros ou processos aceites para apoiar o intercâmbio e o armazenamento de dados transfronteiriços seguros.');
