INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18,  2, 'pt', 'Avaliadas as necessidades de mão-de-obra no domínio da informática em saúde, identificadas as lacunas e em desenvolvimento as opções de formação.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18,  3, 'pt', 'Formação profissional em informática em saúde está disponível, mas os graduados ainda não estão implementados.');
