INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23, -1, 'pt', 'Não disponível');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  1, 'pt', 'Não existe um plano articulado de apoio à infra-estrutura de saúde digital (incluindo equipamentos - computadores/tablets/telefones, materiais, software, dispositivos, etc.) provisão e manutenção.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  2, 'pt', 'Foi desenvolvido, mas não implementado, um plano de apoio às infra-estruturas digitais de saúde (incluindo equipamento - computadores/tabelas/telefones, fornecimentos, software, dispositivos, etc.), fornecimento e manutenção.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  3, 'pt', 'Um plano de apoio às infra-estruturas de saúde digitais (incluindo equipamento - computadores/tabelas/telefones, fornecimentos, software, dispositivos, etc.) e à manutenção foi parcialmente implementado, mas não de forma coerente com a estimativa de 0-25% das infra-estruturas de saúde digitais necessárias no sector dos serviços de saúde pública disponíveis e em utilização.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  4, 'pt', 'Foi implementado um plano de apoio às infra-estruturas digitais de saúde (incluindo equipamento - computadores/tabelas/telefones, fornecimentos, software, dispositivos, etc.) e à manutenção, em parte e de forma coerente com as estimativas de 25-50% das infra-estruturas digitais de saúde necessárias no sector dos serviços públicos de saúde disponíveis e em utilização.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  5, 'pt', 'A infra-estrutura de saúde digital (incluindo equipamentos - computadores/tablets/telefones, suprimentos, software, dispositivos, etc.) está disponível, em uso e é regularmente mantida e atualizada em >75% do setor de serviços públicos de saúde.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24, -1, 'pt', 'Não disponível');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  1, 'pt', 'As áreas prioritárias nacionais não são apoiadas pela saúde digital em nenhuma escala.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  2, 'pt', 'Poucas áreas prioritárias nacionais são apoiadas pela saúde digital, e a implementação foi iniciada (< 25% de áreas prioritárias).');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  3, 'pt', 'Alguns domínios prioritários nacionais apoiados por sistemas de saúde digitais à escala (25-50% dos domínios prioritários).');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  4, 'pt', 'A maioria, mas não todas as áreas prioritárias nacionais (50-75% das áreas prioritárias) são apoiadas por sistemas digitais de saúde em escala.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  5, 'pt', 'Todas as áreas priorizadas a nível nacional apoiadas por sistemas digitais de saúde de escala nacional (>75%) com sistemas de monitorização e avaliação e resultados.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(25, -1, 'pt', 'Não disponível');
