INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES( 1, 'ar', 'هل يوجد في البلد إدارة/ وكالة/ مجموعة عمل وطنية منفصلة للصحة الرقمية؟', 'الصحة الرقمية تحظى بالأولوية على المستوى الوطني من خلال هيئات / آليات إدارة مخصصة');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES( 2, 'ar', 'هل الصحة الرقمية مدرجة في الميزانية أو مدرجة في الخطط الوطنية للصحة أو الاستراتيجيات و/ أو الخطط الوطنية ذات الصلة؟ ملاحظة: ينصب تركيز هذا المؤشر على إدراج الصحة الرقمية أو الصحة الإلكترونية في الاستراتيجية الوطنية للصحة.', 'الأولوية للصحة الرقمية على المستوى الوطني من خلال التخطيط');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES( 3, 'ar', 'هل لدى الدولة استراتيجية أو إطار عمل للصحة الإلكترونية أو الصحة الرقمية وخطة تكلفة للصحة الرقمية؟', 'الإستراتيجية أو إطار العمل الوطني للصحة الإلكترونية أو الرقمية');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES( 4, 'ar', 'ما هي النسبة المئوية المقدرة (٪) من الإنفاق العام السنوي على الصحة والمخصصة للصحة الرقمية؟', 'التمويل العام للصحة الرقمية');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES( 5, 'ar', 'هل هناك قانون لأمن البيانات (التخزين، النقل، الاستخدام) يرتبط بالصحة الرقمية؟', 'الإطار القانوني لحماية البيانات (الأمن)');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES( 6, 'ar', 'هل هناك قانون لحماية خصوصية الأفراد، بحيث يضبط الملكية والوصول إلى وتبادل البيانات الصحية الرقمية التي يمكن تحديدها بشكل فردي؟', 'قوانين أو لوائح الخصوصية والسرية والوصول إلى المعلومات الصحية (الخصوصية)');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES( 7, 'ar', 'هل هناك بروتوكولات أو سياسات أو أطر عمل أو عمليات مقبولة تحكم استخدام الأجهزة الطبية المتصلة بالانترنت والخدمات الصحية الرقمية (مثل التطبيب عن بُعد والتطبيقات) في الرعاية السريرية ورعاية المرضى، لا سيما فيما يتعلق بسلامة وتكامل البيانات وجودة الرعاية؟', 'بروتوكول لتنظيم أو اعتماد الأجهزة و / أو الخدمات الصحية الرقمية');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES( 8, 'ar', 'هل توجد بروتوكولات أو سياسات أو أطر عمل أو عمليات مقبولة لدعم تبادل البيانات وتخزينها بطريقة آمنة عبر الحدود؟ ملاحظة: يتضمن ذلك البيانات المتعلقة بالصحة الواردة إلى بلد ما، وخروجها من بلد ما و / أو استخدامها في بلد ما عندما تتعلق هذه البيانات بشخص من بلد آخر.', 'أمن البيانات عبر الحدود وتبادلها');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES( 9, 'ar', 'هل الصحة الرقمية جزء من المناهج الدراسية لتدريب العاملين الصحيين والعاملين والمساعدين الصحيين، بشكل عام؟', 'الصحة الرقمية مدمجة في الصحة والتدريب المهني ذي الصلة قبل الخدمة (قبل النشر)');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(10, 'ar', 'على وجه التحديد ، هل الصحة الرقمية جزء من المناهج الدراسية للأطباء في التدريب الطبي؟', 'الصحة الرقمية مدمجة في الصحة والتدريب المهني ذي الصلة قبل الخدمة (قبل النشر)');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(11, 'ar', 'على وجه التحديد، هل الصحة الرقمية جزء من المناهج الدراسية لكادر التمريض في التدريب قبل الخدمة؟', 'الصحة الرقمية مدمجة في الصحة والتدريب المهني ذي الصلة قبل الخدمة (قبل النشر)');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(12, 'ar', 'على وجه التحديد، هل الصحة الرقمية جزء من المناهج الدراسية لمهنيي الصحة والمساعدين الصحيين في تدريب العاملين في مجال الصحة المجتمعية؟', 'الصحة الرقمية مدمجة في الصحة والتدريب المهني ذي الصلة قبل الخدمة (قبل النشر)');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(13, 'ar', 'على وجه التحديد، هل تشكل الصحة الرقمية جزءًا من المناهج الدراسية للمساعدين الصحيين في القوى العاملة، بشكل عام؟ [تُعرف باسم العاملين في مجال صحة المجتمع، والممرضين، والأطباء، والمساعدين الصحيين، ومدراء الصحة، والفنيين]' ,'الصحة الرقمية مدمجة في الصحة والتدريب المهني ذي الصلة أثناء الخدمة (بعد النشر)');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(14, 'ar', 'على وجه التحديد، هل الصحة الرقمية جزء من المناهج الدراسية للأطباء؟' ,'الصحة الرقمية مدمجة في الصحة والتدريب المهني ذي الصلة أثناء الخدمة (بعد النشر)');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(15, 'ar', 'على وجه التحديد، هل الصحة الرقمية جزء من المناهج الدراسية للمرضين في القوى العاملة؟', 'الصحة الرقمية مدمجة في الصحة والتدريب المهني ذي الصلة أثناء الخدمة (بعد النشر)');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(16, 'ar', 'على وجه التحديد، هل الصحة الرقمية جزء من المناهج الدراسية للعاملين في مجال الصحة المجتمعية في القوى العاملة؟', 'الصحة الرقمية مدمجة في الصحة والتدريب المهني ذي الصلة أثناء الخدمة (بعد النشر)');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(17, 'ar', 'بشكل عام، هل التدريب في مجال الصحة الرقمية/ المعلوماتية الصحية / نظم المعلومات الصحية / برامج درجة المعلوماتية الطبية الحيوية (في المؤسسات العامة أو الخاصة) التي تخرج العاملين في مجال الصحة الرقمية المدربين؟', 'تدريب القوى العاملة الصحية الرقمية');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(18, 'ar', 'على وجه التحديد ، هل التدريب على المعلوماتية الصحية و / أو الطبية الحيوية (في المؤسسات العامة أو الخاصة) ينتج معلوماتيين مدربين أو متخصصين في نظم المعلومات الصحية؟', 'تدريب القوى العاملة الصحية الرقمية');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(19, 'ar', 'هل هناك مسميات وظيفية ومسارات مهنية للقطاع العام في مجال الصحة الرقمية؟', 'نضج وظائف القطاع العام في مجال الصحة الرقمية');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name) VALUES(20, 'ar', 'الهندسة الصحية الرقمية الوطنية و / أو تبادل المعلومات الصحية');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(21, 'ar', 'هل توجد معايير معلومات الصحة / الصحة الرقمية لتبادل البيانات ونقلها والرسائل والأمن والخصوصية والأجهزة؟', 'معايير المعلومات الصحية');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(22, 'ar', 'استخراج درجة مؤشر استعداد شبكة WEF', 'جاهزية الشبكة');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(23, 'ar', 'هل هناك خطة واضحة لدعم البنية التحتية للصحة الرقمية (بما في ذلك المعدات - أجهزة الكمبيوتر / الأجهزة اللوحية / الهواتف ، اللوازم ، البرامج ، الأجهزة ، إلخ) وتوفيرها وصيانتها؟', 'تخطيط ودعم الصيانة المستمرة للبنية التحتية للصحة الرقمية');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(24, 'ar', 'يتم دعم أولويات القطاع العام (على سبيل المثال ، 14 مجالًا مدرجة في المواصفة القياسية ISO TR 14639) من خلال أنظمة الصحة الرقمية ذات النطاق الوطني. (استخدم ورقة عمل منفصلة لتحديد مجالات الأولوية المحددة للبلد ، وما إذا كانت الأنظمة الرقمية مطبقة ، وما إذا كانت هذه الأنظمة ذات نطاق وطني.) [على سبيل المثال. يختار البلد X 4 مجالات ذات أولوية ، ويستخدم أنظمة رقمية لمعالجة 2 من 4 ، مع وجود مجال واحد فقط على المستوى الوطني ، ويحصل على درجة 25 ٪.]', 'توسيع نظم الصحة الرقمية على الصعيد الوطني');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(25, 'ar', 'هل سجلات النظام الصحي لمزودي الخدمات والمسؤولين والمرافق العامة التي يمكن تحديدها بشكل فريد (والخاصة إذا كان ذلك ممكنًا) متوفرة ومتاحة وحديثة؟ هل تم وضع علامات جغرافية على البيانات لتمكين تعيين GIS؟', 'إدارة الهوية الرقمية لمقدمي الخدمات والمسؤولين والمرافق الخاصة بالصحة الرقمية ، بما في ذلك بيانات الموقع لتعيين نظم المعلومات الجغرافية');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(26, 'ar', 'هل السجلات الآمنة أو مؤشر المريض الرئيسي للأفراد الذين يمكن التعرف عليهم بشكل فريد متاح ويمكن الوصول إليه ومحدث للاستخدام لأغراض تتعلق بالصحة؟', 'إدارة الهوية الرقمية للأفراد من أجل الصحة');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(27, 'ar', 'على وجه التحديد ، هل هناك مؤشر آمن للمريض الرئيسي للأفراد الذين يمكن تحديدهم بشكل فريد ، ويمكن الوصول إليهم واستخدامهم حاليًا لأغراض متعلقة بالصحة؟', 'إدارة الهوية الرقمية للأفراد من أجل الصحة');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(28, 'ar', 'على وجه التحديد ، هل هناك سجل ولادة آمن لأفراد يمكن التعرف عليهم بشكل فريد ومتوفر وحالي للاستخدام لأغراض متعلقة بالصحة؟', 'إدارة الهوية الرقمية للأفراد من أجل الصحة');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(29, 'ar', 'على وجه التحديد ، هل هناك سجل وفاة آمن للأفراد الذين يتم تحديدهم بشكل فريد ، ويمكن الوصول إليهم واستخدامهم حاليًا لأغراض متعلقة بالصحة؟', 'إدارة الهوية الرقمية للأفراد من أجل الصحة');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(30, 'ar', 'على وجه التحديد ، هل يوجد سجل تحصين آمن للأفراد الذين يتم تحديدهم بشكل فريد ومتوفر ومحدث للاستخدام لأغراض متعلقة بالصحة؟', 'إدارة الهوية الرقمية للأفراد من أجل الصحة');
