INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(1,-1,  'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(1 , 1,  'ar', 'لا توجد هيئة تنسيق و/ أو تتكون هيكلية الإدارة الناشئة للصحة الرقمية لكل حالة على حدة.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(1 , 2,  'ar', 'يتم تشكيل هيكلية الإدارة رسميًا على الرغم من أنها لا تغطي كامل الوظائف أو لا تجتمع بانتظام.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(1 , 3,  'ar', 'تتمتع هيكلية الإدارة وأي مجموعات عمل ذات صلة بنطاق عمل. (SOW) وتعقد اجتماعات و/ أو مشاورات منتظمة بمشاركة أصحاب المصلحة');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(1 , 4,  'ar', 'تعمل هيكلية الإدارة بكامل طاقتها، وتتولى الحكومة قيادتها بالتشاور مع الوزارات الأخرى، وتراقب تنفيذ الصحة الرقمية بناءً على خطة العمل.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(1 , 5,  'ar', 'هيكلية إدارة الصحة الرقمية مؤسسية، وتعمل بالتشاور مع الوزارات الأخرى، وترصد تنفيذ الصحة الرقمية. وتحظى بالحماية نسبيا من التدخلات أو التغييرات التنظيمية. كما تحظى بالاعتراف على المستوى الوطني بأنها رائدة للصحة الرقمية. وتركز هيكلية الإدارة وفرق العمل الفنية التابعة لها على التوازن بين الجنسين في العضوية.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 , -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 ,  1, 'ar', 'لا يتم إدراج الصحة الرقمية في الاستراتيجية الوطنية للصحة. ويتم تنفيذها بطريقة مخصصة في البرامج الصحية.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 ,  2, 'ar', 'هناك بعض النقاش حول إدراج الصحة الرقمية في الخطط الوطنية للصحة أو غيرها من الاستراتيجيات أو الخطط الوطنية ذات الصلة. تم وضع نصوص مقترحة لإدراج الصحة الرقمية في الخطط الوطنية للصحة أو الاستراتيجيات و/ أو الخطط الوطنية ذات الصلة وهي قيد المراجعة.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 ,  3, 'ar', 'يتم إدراج الصحة الرقمية في الخطط أو الاستراتيجيات الوطنية للصحة و / أو الخطط الوطنية ذات الصلة.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 ,  4, 'ar', 'يتم تنفيذ الصحة الرقمية كجزء من الخطط الوطنية للصحة أو غيرها من الاستراتيجيات و/ أو الخطط الوطنية ذات الصلة.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(2 ,  5, 'ar', 'يتم تطبيق الصحة الرقمية وتقييمها وتحسينها بشكل دوري في إطار الخطط الوطنية للصحة  أو الاستراتيجيات و/ أو الخطط الوطنية الأخرى ذات الصلة.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(3 , -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(3 ,  1, 'ar', 'لا توجد استراتيجية أو إطار عمل للصحة الرقمية. تم تطوير مسودة إستراتيجية أو إطار عمل للصحة الرقمية، ولكن لم تتم مراجعتها رسميًا');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(3 ,  2, 'ar', 'تم اعتماد استراتيجية أو إطار عمل للصحة الرقمية الوطنية.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(3 ,  3, 'ar', 'تم وضع خطة وطنية للصحة الرقمية وخطة التكلفة والموافقة عليها');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(3 ,  4, 'ar', 'تنفيذ جزئي للاستراتيجية الوطنية للصحة الرقمية وخطة التكلفة مع توفير الموارد لضمان التنفيذ الكامل.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(3 ,  5, 'ar', 'تم تنفيذ الإستراتيجية الوطنية للصحة الرقمية وخطة التكلفة بشكل كامل مع التخطيط الجاري لدورة الـ 3-5 سنوات القادمة.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(4 , -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(4 ,  1, 'ar', 'لا يوجد في الميزانية بند مخصص للصحة الرقمية. يوجد بند مخصص في الميزانية للصحة الرقمية ولكن النسبة غير متوفرة.');
