INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(5 ,  2, 'ar', 'يوجد قانون لأمن البيانات (التخزين، النقل، الاستخدام) يرتبط بالصحة الرقمية تم اقتراحه وهو قيد المراجعة.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(5 ,  3, 'ar', 'يوجد قانون لأمن البيانات (التخزين، النقل، الاستخدام) يرتبط بالصحة الرقمية تم إقراره، لكن لم يتم تنفيذه بالكامل.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(5 ,  4, 'ar', 'يوجد قانون لأمن البيانات (التخزين، النقل، الاستخدام) يرتبط بالصحة الرقمية، وهو مطبق لكن لا يتم إنفاذه بشكل متسق.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(5 ,  5, 'ar', 'يوجد قانون لأمن البيانات (التخزين، النقل، الاستخدام) يرتبط بالصحة الرقمية ويتم تطبيقه وإنفاذه باستمرار.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(6 , -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(6 ,  1, 'ar', 'لا يوجد قانون لحماية خصوصية الأفراد وضبط الملكية والوصول إلى ومشاركة البيانات الصحية الرقمية التي يمكن تحديدها بشكل فردي.');
