INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(6 ,  3, 'ar', 'يوجد قانون لحماية خصوصية الأفراد ويضبط الملكية والوصول إلى ومشاركة البيانات الصحية الرقمية التي يتم تحديدها بشكل فردي تم إقراره ولكن لا يتم إنفاذه بالكامل.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(6 ,  4, 'ar', 'يوجد قانون لحماية خصوصية الأفراد وضبط بالملكية والوصول إلى ومشاركة البيانات الصحية الرقمية التي يتم تحديدها بشكل فردي يتم تنفيذه، ولكن لا يتم تطبيقه بشكل متسق.');
