INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(7 , -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(7 ,  1, 'ar', 'لا توجد بروتوكولات أو سياسات أو أطر عمل أو عمليات مقبولة تحكم استخدام الأجهزة الطبية المتصلة بالانترنت والخدمات الصحية الرقمية (مثل التطبيب عن بُعد والتطبيقات) في الرعاية السريرية ورعاية المرضى، لا سيما فيما يتعلق بسلامة وتكامل البيانات وجودة الرعاية.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(7 ,  2, 'ar', 'تم اقتراح بروتوكولات أو سياسات أو أطر عمل أو عمليات مقبولة تحكم استخدام الأجهزة الطبية المتصلة بالانترنت والخدمات الصحية الرقمية (مثل التطبيب عن بُعد والتطبيقات) في الرعاية السريرية ورعاية المرضى، لا سيما فيما يتعلق بسلامة وتكامل البيانات وجودة الرعاية، وهي قيد المراجعة.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(7 ,  3, 'ar', 'تم اعتماد البروتوكولات والسياسات والأطر أو العمليات المقبولة التي تحكم استخدام الأجهزة الطبية المتصلة بالانترنت والخدمات الصحية الرقمية (مثل التطبيب عن بُعد والتطبيقات) في الرعاية السريرية ورعاية المرضى، لا سيما فيما يتعلق بسلامة وتكامل البيانات وجودة الرعاية، تم إقرارها ولكنها لم تطبق بشكل كامل.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(7 ,  4, 'ar', 'تم تنفيذ البروتوكولات والسياسات والأطر أو العمليات المقبولة التي تحكم استخدام الأجهزة الطبية المتصلة بالانترنت والخدمات الصحية الرقمية (مثل التطبيب عن بُعد والتطبيقات) في الرعاية السريرية ورعاية المرضى، لا سيما فيما يتعلق بسلامة وتكامل البيانات وجودة الرعاية، ولكن لم يتم تطبيقها بشكل متسق.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(7 ,  5, 'ar', 'تم تنفيذ البروتوكولات والسياسات والأطر أو العمليات المقبولة التي تحكم استخدام الأجهزة الطبية المتصلة بالانترنت والخدمات الصحية الرقمية (مثل التطبيب عن بُعد والتطبيقات) في الرعاية السريرية ورعاية المرضى، لا سيما فيما يتعلق بسلامة وتكامل البيانات وجودة الرعاية ويتم تنفيذها بشكل متسق.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(8 , -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(8 ,  1, 'ar', 'لا توجد بروتوكولات أو سياسات أو أطر عمل أو عمليات مقبولة لدعم تبادل البيانات وتخزينها بشكل آمن.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(8 ,  2, 'ar', 'تم اقتراح بروتوكولات أو سياسات أو أطر أو عمليات مقبولة لتبادل البيانات وتخزينها عبر الحدود وهي قيد المراجعة.');
