INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(17, -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(17,  1, 'ar', 'لا يوجد تدريب متاح للعاملين في مجال الصحة الرقمية في البلاد.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(17,  2, 'ar', 'تم تقييم احتياجات العاملين في مجال الصحة الرقمية وتحديد الثغرات ويجري اعداد خيارات التدريب.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(17,  3, 'ar', 'التدريب الاحترافي متاح، لكن لم يتم نشر الخريجين بعد.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(17,  4, 'ar', 'المهنيين المدربين في مجال الصحة الرقمية متوفرين وتم نشرهم، لكن لا تزال هناك ثغرات أساسية في الموظفين.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(17,  5, 'ar', 'توفر أعداد كافية من المتخصصين في مجال الصحة الرقمية المدربين لدعم الاحتياجات في مجال الصحة الرقمية على المستوى الوطني.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18, -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18,  1, 'ar', 'لا يوجد تدريب متاح في مجال المعلوماتية أو نظم المعلومات الصحية المتاحة في البلاد.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18,  2, 'ar', 'تم تقييم احتياجات القوى العاملة في مجال المعلوماتية الصحية وتحديد الثغرات وخيارات التدريب قيد التطوير.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18,  3, 'ar', 'التدريب المهني في مجال المعلوماتية الصحية متاح، لكن لم يتم نشر الخريجين بعد.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18,  4, 'ar', 'مهنيو المعلوماتية المدربون متوفرون وتم نشرهم، لكن لا تزال هناك فجوات أساسية في الموظفين.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(18,  5, 'ar', 'توفر أعداد كافية من المتخصصين في المعلوماتية الصحية المدربين لدعم احتياجات نظام المعلومات الصحية الوطني.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(19, -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(19,  1, 'ar', 'لا توجد إستراتيجية أو سياسة أو دليل للقوى العاملة تعترف بالصحة الرقمية. توزيع القوى العاملة الصحية الرقمية مخصص.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(19,  2, 'ar', 'يُظهر تقييم الاحتياجات الوطنية عدد وأنواع المهارات اللازمة لدعم الصحة الرقمية مع التركيز بشكل واضح على كوادر تدريب العاملات الصحيات.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(19,  3, 'ar', 'يتم تعيين أدوار ومسؤوليات موظفي الصحة الرقمية إلى القوى العاملة وخطط العمل الحكومية و 25-50 ٪ من القوى العاملة الصحية الرقمية المطلوبة في القطاع العام.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(19,  4, 'ar', 'توجد سياسة للموارد البشرية وخطة استراتيجية تحدد المهارات والوظائف اللازمة لدعم الصحة الرقمية مع التركيز بشكل واضح على كوادر تدريب العاملات الصحيات وما يقدر بنحو 50-75 ٪ من القوى العاملة الصحية الرقمية في القطاع العام.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(19,  5, 'ar', 'توجد خطة طويلة الأجل لتنمية ودعم الموظفين الذين يتمتعون بالمهارات اللازمة للحفاظ على الصحة الرقمية على المستويين الوطني ودون الوطني مع التركيز بشكل واضح على كوادر تدريب العاملات في المجال الصحي مع شغل ما يقدر بـ 75٪ من الوظائف المطلوبة. توجد أنظمة لإدارة الأداء لضمان نمو واستدامة القوى العاملة الصحية الرقمية مع توفير ما يكفي لتلبية الاحتياجات الصحية الرقمية وقلة معدل دوران الموظفين.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(20, -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(20,  1, 'ar', 'لا يوجد إطار هيكلي وطني للصحة الرقمية (الصحة الإلكترونية) و / أو تبادل المعلومات الصحية (HIE).');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(20,  2, 'ar', 'تم اقتراح بنية الصحة الرقمية الوطنية و / أو تبادل المعلومات الصحية (HIE) ، ولكن لم تتم الموافقة عليها بما في ذلك الطبقات الدلالية والنحوية والتنظيمية.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(20,  3, 'ar', 'البنية الصحية الرقمية الوطنية و / أو تبادل المعلومات الصحية (HIE) قابلة للتشغيل وتوفر الوظائف الأساسية، مثل المصادقة والترجمة والتخزين، ودليل يظهر البيانات المتاحة وكيفية الوصول إليها، وتفسير البيانات.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(20,  4, 'ar', 'تقوم الحكومة بإدارة وتنفيذ البنية الصحية الرقمية الوطنية و / أو تبادل المعلومات الصحية (HIE)، والتي يتم تنفيذها بالكامل وفقًا للمعايير المعتمدة في هذا القطاع.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(20,  5, 'ar', 'توفر البنية الصحية الرقمية الوطنية و / أو تبادل المعلومات الصحية (HIE) وظائف تبادل البيانات الأساسية وتتم مراجعتها وتحديثها بشكل دوري لتلبية احتياجات بنية الصحة الرقمية المتغيرة. يتوفر التعلم المستمر والابتكار ومراقبة الجودة. يتم استخدام البيانات بنشاط للتخطيط الاستراتيجي الصحي الوطني واعداد الميزانية.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(21, -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(21,  1, 'ar', 'لا توجد معايير للصحة الرقمية / المعلومات الصحية لتبادل البيانات ونقلها والرسائل والأمن والخصوصية والأجهزة.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(21,  2, 'ar', 'توجد بعض معايير المعلومات الصحية / الصحة الرقمية لتبادل البيانات ونقلها والرسائل والأمن والخصوصية والأجهزة التي تم اعتمادها و / أو استخدامها.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(21,  3, 'ar', 'نُشرت معايير المعلومات الصحية / الصحة الرقمية لتبادل البيانات ونقلها والرسائل والأمن والخصوصية والأجهزة في البلاد تحت قيادة الحكومة.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(21,  4, 'ar', 'تستخدم المعايير التقنية القائمة على قطاع المعلومات الصحية / الصحية الرقمية لتبادل البيانات ونقلها والرسائل والأمن والخصوصية والأجهزة في غالبية التطبيقات والأنظمة لضمان توافر البيانات عالية الجودة. يتم إجراء اختبار المطابقة بشكل روتيني للمصادقة على المنفذين.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(21,  5, 'ar', 'يتم تحديث معايير البيانات بشكل روتيني وتستخدم البيانات بنشاط لرصد وتقييم النظام الصحي والتخطيط الاستراتيجي للصحة الوطنية ووضع الميزانية.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(22, -1, 'ar', 'غير متاح');
