INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23, -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  1, 'ar', 'لا توجد خطة واضحة لدعم البنية التحتية للصحة الرقمية (بما في ذلك المعدات - أجهزة الكمبيوتر / الأجهزة اللوحية / الهواتف ، اللوازم ، البرامج ، الأجهزة ، إلخ) وتوفيرها وصيانتها.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  2, 'ar', 'تم تطوير خطة لدعم البنية التحتية للصحة الرقمية (بما في ذلك المعدات - أجهزة الكمبيوتر / الأجهزة اللوحية / الهواتف ، اللوازم ، البرامج ، الأجهزة ، إلخ) ، ولكنها لم تنفذ.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  3, 'ar', 'تم تنفيذ خطة لدعم البنية الأساسية للصحة الرقمية (بما في ذلك المعدات - أجهزة الكمبيوتر / الأجهزة اللوحية / الهواتف ، اللوازم ، البرامج ، الأجهزة ، إلخ) جزئياً ، ولكن هناك 0-25٪ من البنية التحتية الصحية الرقمية اللازمة في قطاع خدمات الرعاية الصحية العامة متاح وقيد الاستخدام.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  4, 'ar', 'تم تنفيذ خطة لدعم البنية التحتية للصحة الرقمية (بما في ذلك المعدات - أجهزة الكمبيوتر / الأجهزة اللوحية / الهواتف ، اللوازم ، البرامج ، الأجهزة ، إلخ) بشكل جزئي ومتسق وهناك 25-50٪ من البنية التحتية الصحية الرقمية اللازمة في الرعاية الصحية العامة قطاع الخدمات متاح وقيد الاستخدام.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(23,  5, 'ar', 'البنية التحتية الصحية الرقمية (بما في ذلك المعدات - أجهزة الكمبيوتر / الأجهزة اللوحية / الهواتف ، اللوازم ، البرامج ، الأجهزة ، وما إلى ذلك) متوفرة ، قيد الاستخدام ، ويتم تحديثها بانتظام في أكثر من 75٪ من قطاع خدمات الرعاية الصحية العامة.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24, -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  1, 'ar', 'مجالات الأولوية الوطنية غير مدعومة بالصحة الرقمية على أي نطاق.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  2, 'ar', 'هناك القليل من مجالات الأولوية الوطنية مدعومة بالصحة الرقمية ، وبدأ التنفيذ (أقل من 25٪ من مجالات الأولوية).');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  3, 'ar', 'توجد بعض المجالات ذات الأولوية الوطنية التي تدعمها أنظمة الصحة الرقمية الموسعة (25-50 ٪ من مجالات الأولوية).');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  4, 'ar', 'غالبية المجالات ذات الأولوية الوطنية (50-75٪ من مجالات الأولوية) ، وليس جميعها ، مدعومة بأنظمة صحية رقمية موسعة.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(24,  5, 'ar', 'جميع المجالات ذات الأولوية الوطنية التي تدعمها أنظمة الصحة الرقمية على المستوى الوطني (> 75 ٪) مع أنظمة الرصد والتقييم والنتائج.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(25, -1, 'ar', 'غير متاح');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(25,  1, 'ar', 'سجلات النظام الصحي لمقدمي الخدمات ، والمسؤولين ، والمرافق العامة التي يمكن تحديدها بشكل فريد (والخاصة إذا كان ذلك ممكنًا) غير متوفرة ومتاحة وحديثة.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES(25,  2, 'ar', 'يجري تطوير سجلات النظام الصحي للموردين والمسؤولين والمرافق العامة التي يمكن تحديدها بشكل فريد (والخاصة إذا كان ذلك ممكنًا) ولكنها غير متاحة للاستخدام.');
