INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(30 , 'fr' ,  'La santé est une priorité dans les politiques nationales de transformation numérique et de gouvernance des données.' ,
'Les politiques et approches nationales de transformation numérique et de gouvernance des données prennent-elles en compte et traitent-elles les avantages et les risques potentiels pour la santé publique et la santé individuelle ?');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(31,  'fr', 'Préparation à l''adoption et à la gouvernance des technologies émergentes',
'Existe-t-il un plan national spécifique aux technologies émergentes (par exemple, IA, Wearables, Blockchain, IoT) pour soutenir les objectifs de santé publique ?');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(32,  'fr', 'Analyse, planification et suivi de la diversité, de l''équité et des droits de l''homme inclus dans les stratégies et plans nationaux de santé numérique',
'Le pays a-t-il évalué/adapté les stratégies nationales de santé numérique du point de vue de l''équité et des droits de l''homme ?');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(33,  'fr', 'Prise en compte des considérations de genre dans les stratégies de santé numérique et la gouvernance de la santé numérique',
'Le pays intègre-t-il des considérations de genre dans la stratégie nationale de santé numérique ou sa gouvernance de la santé numérique ?');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(37,  'fr', 'Alignement de la stratégie numérique nationale sur les composantes essentielles de la couverture sanitaire universelle (CSU)',
'La stratégie nationale de santé numérique du pays est-elle alignée sur la couverture sanitaire universelle et/ou les composantes essentielles de la CSU ?');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(38,  'fr', 'Participation et investissements du secteur privé dans la santé numérique',
'Compte tenu de l''environnement favorable, le secteur privé participe-t-il et investit-il dans les activités de santé numérique ?');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(36,  'fr', 'Protocole de régulation et de certification de l''IA au sein des services de santé',
'Existe-t-il des protocoles, des politiques, des cadres ou des processus acceptés régissant l''utilisation de l''IA dans les systèmes, services et applications de santé, en particulier en ce qui concerne l''éthique, l''équité, la sécurité, l''intégrité des données et la qualité des soins ?');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(34,  'fr', 'Sécuriser les systèmes de rétroaction des patients',
'Plus précisément, existe-t-il un système sécurisé de retour d''information des patients, disponible, accessible ?');
INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(35,  'fr', 'Contribution de la santé numérique à la gestion de la santé de la population',
'Les initiatives nationales actuelles en matière de santé numérique contribuent-elles aux rapports et à la prise de décision en matière de santé publique ?');








