INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 30,-1,  'fr', 'Non disponible');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 30, 1,  'fr', 'Les politiques nationales de transformation numérique et de gouvernance des données n''incluent pas les avantages et les risques potentiels pour la santé publique ou les résultats de santé individuels.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 30, 2,  'fr', 'Les politiques nationales de transformation numérique et de gouvernance des données incluent les avantages et les risques potentiels pour la santé publique ou les résultats de santé individuels sur une base ad hoc.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 30, 3,  'fr', 'Les politiques nationales de transformation numérique et de gouvernance des données incluent systématiquement les avantages et les risques potentiels pour la santé publique ou les résultats de santé individuels, mais n''ont pas de stratégie(s) pour y faire face.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 30, 4,  'fr', 'Les politiques nationales de transformation numérique et de gouvernance des données incluent systématiquement les avantages et les risques potentiels pour la santé publique ou les résultats de santé individuels et ont une ou plusieurs stratégies pour y faire face qui ne sont pas encore mises en œuvre.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 30, 5,  'fr', 'Toutes les politiques pertinentes de transformation numérique et de gouvernance des données tiennent pleinement compte des avantages et des risques potentiels pour les systèmes de santé, les déterminants de la santé et les résultats de santé individuels (y compris pour les enfants, les femmes et les groupes vulnérables) et disposent de stratégies multisectorielles pleinement mises en œuvre.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 31,-1,  'fr', 'Non disponible');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 31, 1,  'fr', 'Il n''y a pas de plan de technologies émergentes (par exemple, IA, Wearables, Blockchain, IoT) à l''appui des objectifs de santé publique.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 31, 2,  'fr', 'Un plan a été élaboré pour au moins une technologie émergente (par exemple, IA, Wearables, Blockchain, IdO) pour soutenir les objectifs de santé publique, mais il n''est pas mis en œuvre.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 31, 3,  'fr', 'Un plan existe pour au moins une technologie émergente (par exemple, IA, Wearables, Blockchain, IoT) pour soutenir les objectifs de santé publique. Le ou les plans identifient les mécanismes de gouvernance requis pour les technologies émergentes.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 31, 4,  'fr', 'Un plan pour une ou plusieurs technologies émergentes (par exemple, IA, Wearables, Blockchain, IoT) pour soutenir la santé publique est mis en œuvre, est financé et les résultats sont surveillés. Les mécanismes de gouvernance requis pour les technologies émergentes sont en place.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 31, 5,  'fr', 'Un plan pour une ou plusieurs technologies émergentes (par exemple, IA, Wearables, Blockchain, IoT) à l''appui de la santé publique est mis en œuvre, est financé, les résultats sont surveillés et le plan est tenu à jour. Des mécanismes de gouvernance sont en place et utilisés.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 32,-1,  'fr', 'Non disponible');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 32, 1,  'fr', 'Les stratégies et programmes de santé numérique sont élaborés et mis en œuvre sans tenir compte des implications en matière d''équité et de droits de l''homme.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 32, 2,  'fr', 'Des stratégies et des programmes de santé numérique sont élaborés et mis en œuvre en tenant compte au cas par cas des implications en matière d''équité et de droits de l''homme.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 32, 3,  'fr', 'Les stratégies et programmes de santé numérique sont élaborés et mis en œuvre en tenant compte formellement des implications en matière d''équité et de droits de l''homme, sans stratégie(s) pour y faire face.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 32, 4,  'fr', 'Des stratégies et des programmes de santé numérique sont élaborés et mis en œuvre en tenant compte formellement des implications en matière d''équité et de droits de l''homme avec une ou des stratégies ad hoc pour y faire face.');
INSERT INTO i18n.score_definition(indicator_id, score, language_id, definition) VALUES( 32, 5,  'fr', 'Le pays met en œuvre et évalue les effets des stratégies de santé numérique et des solutions de santé numérique spécifiques basées sur des évaluations d''impact sur l''équité et les droits de l''homme. Des stratégies documentées sont en place pour combler les lacunes en matière d''accès et de résultats pour différents groupes de population, y compris les femmes, les enfants et les groupes marginalisés.');
