INSERT INTO i18n.health_indicator(indicator_id, language_id, definition, name) VALUES( 30, 'ar', 'هل تراعي السياسات والنهج الوطنية للتحول الرقمي وحوكمة البيانات الفوائد والمخاطر المحتملة على الصحة العامة وصحة الفرد وتعالجها؟' , 'ا إعطاء الأولوية للصحة في السياسات الوطنية للتحول الرقمي وإدارة البيانات.');
INSERT INTO i18n.health_indicator(indicator_id, language_id, definition, name) VALUES( 31, 'ar', 'هل هناك خطة وطنية خاصة بالتكنولوجيات الناشئة (مثل الذكاء الاصطناعي والأجهزة القابلة للارتداء وسلسلة الكتل "البلوك تشين" وإنترنت الأشياء) لدعم أهداف الصحة العامة؟' , 'ا الجاهزية لاعتماد التكنولوجيات الناشئة وإدارتها');
INSERT INTO i18n.health_indicator(indicator_id, language_id, definition, name) VALUES( 32, 'ar', 'هل قام البلد بتقييم/تكييف الاستراتيجيات الوطنية للصحة الرقمية بالاعتماد على منظور العدالة وحقوق الإنسان؟' , 'ا يتم ادراج تحليل وتخطيط ورصد التنوع والعدالة وحقوق الإنسان في الاستراتيجيات والخطط الوطنية للصحة الرقمية');
INSERT INTO i18n.health_indicator(indicator_id, language_id, definition, name) VALUES( 33, 'ar', 'هل تدرج الدولة اعتبارات النوع الاجتماعي في الاستراتيجية الوطنية للصحة الرقمية أو حوكمة الصحة الرقمية؟' , 'ا مراعاة اعتبارات المساواة بين الجنسين في استراتيجيات وإدارة الصحة الرقمي');
INSERT INTO i18n.health_indicator(indicator_id, language_id, definition, name) VALUES( 37, 'ar', 'هل تتماشى الاستراتيجية الوطنية للصحة الرقمية في البلد مع التغطية الصحية الشاملة و/أو المكونات الأساسية للتغطية الصحية الشاملة؟' , 'ا مواءمة الاستراتيجية الرقمية الوطنية مع المكونات الأساسية للتغطية الصحية الشاملة');
INSERT INTO i18n.health_indicator(indicator_id, language_id, definition, name) VALUES( 38, 'ar', 'بالنظر إلى البيئة التمكينية، هل يشارك القطاع الخاص ويستثمر في أنشطة الصحة الرقمية؟' , 'ا مشاركة القطاع الخاص واستثماراته في الصحة الرقمية');
INSERT INTO i18n.health_indicator(indicator_id, language_id, definition, name) VALUES( 36, 'ar', 'هل هناك بروتوكولات أو سياسات أو أطر أو عمليات مقبولة تحكم استخدام الذكاء الاصطناعي في النظم والخدمات والتطبيقات الصحية، لا سيما فيما يتعلق بالأخلاقيات والعدالة والسلامة وسلامة البيانات وجودة الرعاية؟' , 'ا بروتوكول لتنظيم واعتماد الذكاء الاصطناعي في مجال الخدمات الصحية');
INSERT INTO i18n.health_indicator(indicator_id, language_id, definition, name) VALUES( 34, 'ar', 'على وجه التحديد، هل هناك نظام آمن لتلقي التغذية الراجعة من المرضى، وهو متاح ويمكن الوصول إليه' , 'ا نظام آمن لتلقي التغذية الراجعة من المرضى');
INSERT INTO i18n.health_indicator(indicator_id, language_id, definition, name) VALUES( 35, 'ar', 'هل تساهم مبادرات الدول للصحة الرقمية الحالية في إعداد تقارير الصحة العامة واتخاذ القرارات؟', 'ا مساهمة الصحة الرقمية في إدارة صحة السكان');