INSERT INTO i18n.health_indicator(indicator_id, language_id, name, definition) VALUES(32,  'es', 'Análisis, planificación y seguimiento de la diversidad, la equidad y los derechos humanos incluidos en las estrategias y planes nacionales de salud digital',
